css:
body {
  font-family: 'Segoe UI', sans-serif;
  margin: 20px;
  background: #fff;
  color: #000;
}

.report-container {
  width: 100%;
  margin: auto;
}

h2 {
  text-align: center;
  margin-bottom: 20px;
}

.report-table {
  width: 100%;
  border-collapse: collapse;
}

.report-table th, .report-table td {
  border: 1px solid #333;
  padding: 8px;
  text-align: left;
}

.report-table th {
  background-color: #f2f2f2;
}

.report-table tr:nth-child(even) {
  background-color: #f9f9f9;
}
